CABLES.OPS["5f6c2a84-8de9-41e5-948a-d9c5ed49022f"]={f:Ops.Extension.AmmoPhysics.AmmoCharacter,objName:"Ops.Extension.AmmoPhysics.AmmoCharacter"};




